import React from 'react'

export default class FetchAPICard extends React.Component {
    state = {
        loading : true,
        cardList: null,
    };
    async componentDidMount(){
        const url = "http://localhost:3000/carListData";
        const response = await fetch(url);
        const data =  await response.json();
        this.setState({CardList: data[0], loading : false});
    }
    render (){
        return <div>
            {
                this.state.loading || !this.state.cardList?
                (<div>loading..</div>):
                (
                    
                    <div className="row">
                    {data.map((cardList,index) => {
                        //let setClass = (cardList.title).toLowerCase().replace(/ /g,'');
                        return <div className="col-4 card-wrapper mt-2 mb-4">
                            <div className="card pb-0 box">
                            <h4 className="text-left">{cardList.title}</h4>
                            <p className="des text-left">{cardList.description}</p>
                            <div className='linkBox row bottom-xs'>
                            </div>
                            </div>
                            </div>
                    })}
                </div>

                )
            }
        </div>
    }
}
