import React from 'react'

export default class FetchAPICard extends React.Component {
    state = {
        loading : true,
        CardList: null,
    };
    async componentDidMount(){
        const url = "http://localhost:3000/carListData";
        const response = await fetch(url);
        const data =  await response.json();
        this.setState({title: data[0], loading : false});
    }
    render (){
        return <div>
            {
                this.state.loading || !this.state.title?
                (<div>loading..</div>):
                (<div>{this.state.title.title}</div>)
            }
        </div>
    }
}
